
public class Exe03 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("i=31,j=13,在不同数据型下的运算结果为：");
		int i=31,j=13;
		System.out.println("int型：i+j="+(i+j)+" i*j="+(i*j)+" i/j="+(i/j));
		short a=31,b=13;
		System.out.println("short型：i+j="+(a+b)+" i*j="+(a*b)+" i/j="+(a/b));
		long c=31,d=13;
		System.out.println("long型：i+j="+(c+d)+" i*j="+(c*d)+" i/j="+(c/d));
		float e=31,f=13;
		System.out.println("int型：i+j="+(e+f)+" i*j="+(e*f)+" i/j="+(e/f));
		double g=31,h=13;
		System.out.println("int型：i+j="+(g+h)+" i*j="+(g*h)+" i/j="+(g/h));
		
	}

}
